import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {
    items: [],
    totalQuantity: 0,
  },
  reducers: {
    addItemCart(state, action) {
      const item = action.payload;
    },
    removeItemCart() {},
  },
});
